mapper.select:
select
from um_application u where u.status <> 9 and u.id in
#{id}

SELECT
	
FROM
	um_application u
WHERE
	u.status <> 9 
	 AND u.code = #{page.cnds.code}
	 AND u.type = #{page.cnds.type}
	 AND u.clazz = #{page.cnds.clazz}
	 AND u.screenType = #{page.cnds.screenType}
	 AND u.mappingType = #{page.cnds.mappingType}
     AND u.name like #{page.cnds.name}
	 AND u.status = #{page.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	
FROM
	um_application u
	
		 JOIN um_kh_rel_company_app rcp ON u.id=rcp.appId 
	
WHERE
	u.status <> 9 
	
		 AND u.clazz=0 AND u.id not in ( SELECT rel.appId FROM um_yw_rel_role_app rel WHERE rel.roleId = #{page.cnds.roleId} ) 
	
	
		 AND u.clazz=1 AND u.id not in ( SELECT rel.appId FROM um_kh_rel_role_app rel WHERE rel.roleId = #{page.cnds.roleId} ) 
	
	
		 AND rcp.companyId=#{page.cnds.khCompanyId} AND u.id not in ( SELECT rel.appId FROM um_kh_rel_role_app rel WHERE rel.roleId = #{page.cnds.roleId} ) 
	
	
		 AND rcp.companyId=#{page.cnds.rootKhCompanyId}
	
	
		 AND u.id not in ( SELECT rel.appId FROM um_kh_rel_company_app rel WHERE rel.companyId = #{page.cnds.khCompanyId} ) 
	
	 AND u.code = #{page.cnds.code}
	 AND u.type = #{page.cnds.type}
	 AND u.clazz = #{page.cnds.clazz}
	 AND u.screenType = #{page.cnds.screenType}
	 AND u.mappingType = #{page.cnds.mappingType}
     AND u.name like #{page.cnds.name}
	 AND u.status = #{page.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.id

