mapper.update:
update 
	um_application 
set 
	name=#{entity.name},sort=#{entity.sort},url=#{entity.url},logout=#{entity.logout},remark=#{entity.remark},screenType=#{entity.screenType}
	 ,mappingType=#{entity.mappingType}
	 ,ico=#{entity.ico}
	 ,status=#{entity.status}
	,updateTime=now()
where 
	id=#{entity.id} and uuid=#{entity.uuid}

mapper.select:
SELECT
FROM um_application u join um_yw_rel_role_app rel on u.id=rel.appId WHERE u.status=0 And u.clazz=0 AND rel.roleId = #{page.cnds.roleId}
order by u.${ord}
order by u.id

SELECT
	
FROM um_application u 
	
		 JOIN um_yw_rel_company_app rcp ON u.id=rcp.appId 
	
WHERE
	u.status <> 9 
	AND u.clazz=0 
	
		 AND rcp.companyId=#{page.cnds.companyId}
	
	AND u.id not in ( SELECT rel.appId FROM um_yw_rel_role_app rel WHERE rel.roleId = #{page.cnds.roleId} ) 
	 AND u.code = #{page.cnds.code}
	 AND u.type = #{page.cnds.type}
	 AND u.clazz = #{page.cnds.clazz}
	 AND u.screenType = #{page.cnds.screenType}
	 AND u.mappingType = #{page.cnds.mappingType}
     AND u.name like #{page.cnds.name}
	 AND u.status = #{page.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
FROM um_application u join um_kh_rel_role_app rel on u.id=rel.appId WHERE u.status=0 And u.clazz=1 AND rel.roleId = #{page.cnds.roleId}
order by u.${ord}
order by u.id

