SELECT
	
FROM
	um_application u 
	join um_kh_rel_company_app rel on u.id=rel.appId
	join um_kh_company c on rel.companyId=c.id 
WHERE
	u.status=0 And u.clazz=1 
	
         AND c.area1Id in #{areaId} 
         AND c.area2Id in #{areaId} 
         AND c.area3Id in #{areaId} 
         AND c.area4Id in #{areaId} 
         AND c.area5Id in #{areaId} 
         AND c.area6Id in #{areaId} 
         AND c.area7Id in #{areaId} 
         AND c.area8Id in #{areaId} 
    
	 AND rel.companyId = #{page.cnds.khCompanyId} 
	 AND c.parentId = #{page.cnds.parentCompanyId} 
	 AND u.code = #{page.cnds.code}
	 AND u.name like #{page.cnds.name}
	 AND u.type = #{page.cnds.type}
	 AND u.clazz = #{page.cnds.clazz}
	 AND u.screenType = #{page.cnds.screenType}
	 AND u.mappingType = #{page.cnds.mappingType}
order by rel.companyId

SELECT
	
FROM um_application u 
	
		left join um_kh_rel_company_app rel on u.id=rel.appId
	
WHERE
	u.status=0 And u.clazz=1 
	
		AND rel.companyId = #{page.cnds.parentCompanyId}
	
	AND u.id not in ( SELECT rel2.appId FROM um_kh_rel_company_app rel2 WHERE rel2.companyId = #{page.cnds.khCompanyId} ) 
	 AND u.code = #{page.cnds.code}
	 AND u.name like #{page.cnds.name}
	 AND u.type = #{page.cnds.type}
	 AND u.clazz = #{page.cnds.clazz}
	 AND u.screenType = #{page.cnds.screenType}
	 AND u.mappingType = #{page.cnds.mappingType}

