mapper.select:
select
from um_kh_department u where u.status <> 9 and u.id in
#{id}

SELECT
	
FROM
	um_kh_department u 
WHERE
	u.status <> 9 
     AND u.address like concat('%',#{page.cnds.address},'%')
	 AND u.name like #{page.cnds.name}
	 AND u.status = #{page.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	
FROM
	um_kh_department u
WHERE
	u.status <> 9 
	 AND u.type = #{query.type}
     AND u.name like #{page.cnds.name}
	 AND u.status = #{query.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.id

mapper.update:
update 
	um_kh_department 
set 
	sort=#{entity.sort},type=#{entity.type},ico=#{entity.ico},remark=#{entity.remark},
	linkUserId=#{entity.linkUserId},linkUserName=#{entity.linkUserName},linkUserPhone=#{entity.linkUserPhone},
	name=#{entity.name},fullName=#{entity.fullName},level=#{entity.level},updateTime=now()
	 ,status=#{entity.status}
	 ,parentId=#{entity.parentId},parentClass=#{entity.parentClass}
	 ,code=#{entity.code}
where 
	id=#{entity.id} and uuid=#{entity.uuid}

mapper.select:
select 
from   um_kh_department u 
where  u.status <> 9 
	AND substring(u.code, 1, LENGTH(#{parentDepartmentCode})) = #{parentDepartmentCode}
	AND u.code <> #{parentDepartmentCode}
order by u.sort asc
