mapper.select:
select
from um_kh_user_jz u where u.status <> 9 and u.id in
#{id}

SELECT

FROM
	um_kh_user_jz u 
	JOIN um_kh_user ku ON u.userId = ku.id AND ku.status <> 9 
WHERE
	u.status <> 9 
	 AND ku.status = #{page.cnds.userStatus}
	 AND ku.status <> #{page.cnds.userStatusNe}
	 AND u.srcCompanyId = #{page.cnds.srcCompanyId}
	 AND u.srcOrgId = #{page.cnds.srcOrgId}
	 AND u.srcOrgClass = #{page.cnds.srcOrgClass}
	 AND u.srcOrgName like #{page.cnds.srcOrgName}
	 AND u.userId = #{page.cnds.userId}
	 AND u.userName like #{page.cnds.userName}
	 AND u.destCompanyId = #{page.cnds.destCompanyId}
	 AND u.destOrgId = #{page.cnds.destOrgId}
	 AND u.destOrgClass = #{page.cnds.destOrgClass}
	 AND u.destOrgName like #{page.cnds.destOrgName}
	 AND u.createTime >= #{page.cnds.createTime.start} AND u.createTime <= #{page.cnds.createTime.end}


		order by  u.${ord} 


		order by u.parentId

SELECT

FROM
	um_kh_user_jz u
	JOIN um_kh_user ku ON u.userId = ku.id AND ku.status <> 9 AND ku.status <> 8 
WHERE
	u.status <> 9
	 AND ku.status = #{query.cnds.userStatus}
	 AND ku.status <> #{query.cnds.userStatusNe}
	 AND u.srcCompanyId = #{query.cnds.srcCompanyId}
	 AND u.srcOrgId = #{query.cnds.srcOrgId}
	 AND u.srcOrgClass = #{query.cnds.srcOrgClass}
	 AND u.srcOrgName like #{query.cnds.srcOrgName}
	 AND u.userId = #{query.cnds.userId}
	 AND u.userName like #{query.cnds.userName}
	 AND u.destCompanyId = #{query.cnds.destCompanyId}
	 AND u.destOrgId = #{query.cnds.destOrgId}
	 AND u.destOrgClass = #{query.cnds.destOrgClass}
	 AND u.destOrgName like #{query.cnds.destOrgName}
	 AND u.createTime >= #{query.cnds.createTime.start} AND u.createTime <= #{query.cnds.createTime.end}


		order by  u.${ord} 


		order by u.id
