mapper.select:
select
from um_kh_role u where u.status <> 9 and u.id in
#{id}

SELECT
	
FROM
	um_kh_role u left join um_kh_company d on u.parentId=d.id 
WHERE
	u.status <> 9 
	 AND u.type = #{page.cnds.type}
	 AND u.companyId = #{page.cnds.companyId}
	 AND u.parentId = #{page.cnds.parentId}
	 AND u.name like #{page.cnds.name}
	 AND u.govCode like #{page.cnds.govCode}
	 AND u.status = #{page.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	
FROM
	um_kh_role u 
WHERE
	u.status <> 9
	 AND u.govCode = #{query.cnds.govCode}
	 AND u.type = #{query.cnds.type}
	 AND u.companyId = #{query.cnds.companyId}
	 AND u.parentId = #{query.cnds.parentId}
	 AND u.name like #{query.cnds.name}
	 AND u.status = #{query.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	
FROM
	um_kh_role u 
WHERE
	u.status <> 9  AND ( u.companyId = #{companyId} OR u.type = 9 ) 
	 AND u.name like #{cnds.name}
	 AND u.govCode like #{cnds.govCode}
	 AND u.status = #{cnds.status}

SELECT
	
FROM
	um_kh_role u 
WHERE
	u.status=0 
	
		 AND u.id not in ( SELECT rel.roleId FROM um_kh_rel_role_user rel WHERE rel.userId = #{page.cnds.userId} ) 
	
	 
		AND ( u.companyId = #{page.cnds.companyId} OR u.type = 9 )
	
	 AND u.level <> #{page.cnds.levelNotEqual}
	 AND u.type = #{page.cnds.type}
	 AND u.parentId = #{page.cnds.parentId}
	 AND u.name like #{page.cnds.name}
	 AND u.govCode like #{page.cnds.govCode}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	
FROM
	um_kh_role u join um_kh_rel_role_user rel on u.id=rel.roleId 
WHERE
	u.status=0 AND rel.userId=#{page.cnds.userId}
	 AND u.type = #{page.cnds.type}
	 AND u.companyId = #{page.cnds.companyId}
	 AND u.parentId = #{page.cnds.parentId}
	 AND u.name like #{page.cnds.name}
	 AND u.govCode like #{page.cnds.govCode}

	
		order by  u.${ord} 
	
	
		order by u.id

mapper.update:
update 
	um_kh_role 
set 
	name=#{entity.name},sort=#{entity.sort},ico=#{entity.ico},remark=#{entity.remark},updateTime=now()
	 ,parentId=#{entity.parentId},parentClass=#{entity.parentClass}
	 ,status=#{entity.status}
	 ,code=#{entity.code}
	 ,govCode=#{entity.govCode}
	 ,level=#{entity.level}
where 
	id=#{entity.id} and uuid=#{entity.uuid}
