mapper.select:
select
from um_kh_user u left join um_kh_department d on u.parentId=d.id AND u.parentClass='KhDepartment' left join um_kh_company c on u.companyId=c.id where u.id in
#{id}

SELECT
	
FROM
	um_kh_user u 
	left join um_kh_department d on u.parentId=d.id AND u.parentClass='KhDepartment' 
	left join um_kh_company c on u.companyId=c.id 
WHERE
	u.status <> 9 AND u.account <> 'superadmin' 
	
         AND c.area1Id in #{areaId} 
         AND c.area2Id in #{areaId} 
         AND c.area3Id in #{areaId} 
         AND c.area4Id in #{areaId} 
         AND c.area5Id in #{areaId} 
         AND c.area6Id in #{areaId} 
         AND c.area7Id in #{areaId} 
         AND c.area8Id in #{areaId} 
    
	 AND u.areaId = #{page.cnds.areaId}
	 AND u.areaName like #{page.cnds.areaName}
	 AND u.companyId = #{page.cnds.companyId}
	 AND u.parentId = #{page.cnds.parentId} AND u.parentClass='KhDepartment'
	 AND u.name like #{page.cnds.name}
	 AND u.sex = #{page.cnds.sex}
	 AND u.account like #{page.cnds.account}
	 AND u.mobile like #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}
	 AND u.status <> #{page.cnds.statusNe}

	
		order by  u.${ord} 
	
	
		order by u.parentId

SELECT
	
FROM
	um_kh_user u 
	left join um_kh_department d on u.parentId=d.id AND u.parentClass='KhDepartment' 
	left join um_kh_company c on u.companyId=c.id 
WHERE
	u.status <> 9 AND u.account <> 'superadmin' 
	 AND u.companyId = #{page.cnds.companyId}
	 AND u.parentId = #{page.cnds.parentId} AND u.parentClass='KhDepartment' 
	 AND u.name like #{page.cnds.name}
	 AND u.sex = #{page.cnds.sex}
	 AND u.account like #{page.cnds.account}
	 AND u.mobile like #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}
	 AND u.status <> #{page.cnds.statusNe}

	
		order by  u.${ord} 
	
	
		order by u.parentId

SELECT
		u.*
FROM (
				SELECT
					ku.id,ku.uuid,ku.status,ku.createTime,ku.updateTime,ku.companyId,
					case when ku.parentClass='KhDepartment' then ku.parentId
					else ku.parentId*-1
					end parentId,
					ku.parentClass,ku.account,ku.name,ku.nickName,ku.mobile,ku.idCard,ku.sex,ku.birthday,ku.remark,ku.lastLoginTime,
					ku.email,ku.sort,ku.type,ku.ico,ku.code,ku.govCode,d.name as orgName,d.fullName as orgFullName,c.name as companyName
				FROM
					um_kh_user ku 
					LEFT JOIN um_kh_department d on ku.parentId=d.id AND ku.parentClass='KhDepartment' 
					LEFT JOIN um_kh_company c on ku.companyId=c.id 
				WHERE
					ku.status <> 9 AND ku.status <> 8 AND ku.account <> 'superadmin' 
					AND ku.companyId = #{page.cnds.companyId}
		) u
		JOIN um_kh_perm_org p ON u.parentId=p.orgId
		JOIN um_kh_rel_role_user rel ON p.roleId=rel.roleId
WHERE	
		p.appId=#{page.cnds.appId} AND rel.userId=#{page.cnds.userId}
		 AND u.name like #{page.cnds.name}
		 AND u.sex = #{page.cnds.sex}
		 AND u.account like #{page.cnds.account}
		 AND u.mobile like #{page.cnds.mobile}
		 AND u.status = #{page.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.parentId

SELECT
	u.id,u.uuid,u.status,u.createTime,u.updateTime,
	u.companyId,u.parentId,u.parentClass,u.account,u.password,u.salt,u.name,u.nickName,u.mobile,u.idCard,u.sex,u.birthday,u.remark,u.lastLoginTime,
	u.email,u.sort,u.type,u.ico,u.code,u.govCode,u.jobPosition,u.job,u.sms,u.zwuid,u.dduid,u.ddStatus,d.name as orgName,d.fullName as orgFullName,c.name as companyName
FROM um_kh_user u 
	left join um_kh_department d on u.parentId=d.id AND u.parentClass='KhDepartment' 
	left join um_kh_company c on u.companyId=c.id 
WHERE
	u.status <> 9 AND u.status <> 8 AND u.account <> 'superadmin' 
	
         AND c.area1Id in #{areaId} 
         AND c.area2Id in #{areaId} 
         AND c.area3Id in #{areaId} 
         AND c.area4Id in #{areaId} 
         AND c.area5Id in #{areaId} 
         AND c.area6Id in #{areaId} 
         AND c.area7Id in #{areaId} 
         AND c.area8Id in #{areaId} 
    
	
		 AND u.id not in ( SELECT rel.userId FROM um_kh_rel_role_user rel WHERE rel.roleId = #{page.cnds.roleId} ) 
	
	 
		AND u.companyId = #{page.cnds.companyId}
	
	 
		AND u.companyId in (
			SELECT com.id FROM um_kh_company com WHERE com.parentId=#{page.cnds.companyId} OR com.id=#{page.cnds.companyId}
		) 
	
	 AND u.parentId = #{page.cnds.parentId}
	 AND u.name like #{page.cnds.name}
	 AND u.sex = #{page.cnds.sex}
	 AND u.account like #{page.cnds.account}
	 AND u.mobile like #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.parentId

SELECT
		u.*
FROM (
				SELECT
					ku.id,ku.uuid,ku.status,ku.createTime,ku.updateTime,ku.companyId,
					case when ku.parentClass='KhDepartment' then ku.parentId
					else ku.parentId*-1
					end parentId,
					ku.parentClass,ku.account,ku.name,ku.nickName,ku.mobile,ku.idCard,ku.sex,ku.birthday,ku.remark,ku.lastLoginTime,
					ku.email,ku.sort,ku.type,ku.ico,ku.code,ku.govCode,d.name as orgName,d.fullName as orgFullName,c.name as companyName
				FROM
					um_kh_user ku 
						LEFT JOIN um_kh_department d on ku.parentId=d.id AND ku.parentClass='KhDepartment' 
						LEFT JOIN um_kh_company c on ku.companyId=c.id 
				WHERE
					ku.status <> 9 AND ku.status <> 8 AND ku.account <> 'superadmin' 
					AND ku.companyId = #{page.cnds.companyId}
		) u
		JOIN um_kh_perm_org p ON u.parentId=p.orgId
		JOIN um_kh_rel_role_user rel ON p.roleId=rel.roleId
WHERE	
		p.appId=#{page.cnds.appId} AND rel.userId=#{page.cnds.userId}
	
		 AND u.id not in ( SELECT rel.userId FROM um_kh_rel_role_user rel WHERE rel.roleId = #{page.cnds.roleId} ) 
	
	 
		AND u.companyId = #{page.cnds.companyId}
	
	 
		AND u.companyId in (
			SELECT com.id FROM um_kh_company com WHERE com.parentId=#{page.cnds.companyId} OR com.id=#{page.cnds.companyId}
		) 
	
	 AND u.parentId = #{page.cnds.parentId}
	 AND u.name like #{page.cnds.name}
	 AND u.sex = #{page.cnds.sex}
	 AND u.account like #{page.cnds.account}
	 AND u.mobile like #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.parentId

SELECT
	
FROM
	um_kh_user u 
	left join um_kh_department d on u.parentId=d.id AND u.parentClass='KhDepartment' 
	left join um_kh_company c on u.companyId=c.id 
	join um_kh_rel_role_user rel on u.id=rel.userId
WHERE
	u.status <> 9 AND u.status <> 8 AND rel.roleId = #{page.cnds.roleId}
	 
		AND u.companyId = #{page.cnds.companyId}
	
	 
		AND u.companyId in (
			SELECT com.id FROM um_kh_company com WHERE com.parentId=#{page.cnds.companyId} OR com.id=#{page.cnds.companyId}
		) 
	
	 AND u.parentId = #{page.cnds.parentId}
	 AND u.name like #{page.cnds.name}
	 AND u.mobile like #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	
FROM
	um_kh_user u 
	left join um_kh_department d on u.parentId=d.id AND u.parentClass='KhDepartment' 
	left join um_kh_company c on u.companyId=c.id 
WHERE
	u.status <> 9
	 AND u.companyId = #{query.cnds.companyId}
	 AND u.parentId = #{query.cnds.parentId}
	 AND u.name like #{query.cnds.name}
	 AND u.sex = #{query.cnds.sex}
	 AND u.account like #{query.cnds.account}
	 AND u.mobile like #{query.cnds.mobile}
	 AND u.status = #{query.cnds.status}
	 AND u.status <> #{query.cnds.statusNe}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	u.id,u.uuid,u.status,u.createTime,u.updateTime,u.parentId,u.parentClass,u.account,u.salt,
	u.name,u.nickName,u.mobile,u.idCard,u.sex,u.birthday,u.remark,u.lastLoginTime,
	u.email,u.type,u.ico,u.code,u.govCode,rel.jobPosition,rel.sort
FROM
	um_kh_user u join um_kh_rel_org_leader rel on u.id=rel.userId 
WHERE
	u.status <> 9 AND u.status <> 8 AND rel.orgId=#{page.cnds.orgId} 
	 AND u.name like #{page.cnds.name}
	 AND u.sex = #{page.cnds.sex}
	 AND u.mobile like #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}
order by rel.sort

mapper.update:
update 
	um_kh_user 
set 
	name=#{entity.name},nickName=#{entity.nickName},mobile=#{entity.mobile},sex=#{entity.sex},idCard=#{entity.idCard},birthday=#{entity.birthday},
	jobPosition=#{entity.jobPosition},job=#{entity.job},sms=#{entity.sms}
	 ,companyId=#{entity.companyId}
	 ,parentId=#{entity.parentId},parentClass=#{entity.parentClass} 
	 ,password=#{entity.password},salt=#{entity.salt} 
	 ,status=#{entity.status}
	 ,code=#{entity.code}
	 ,govCode=#{entity.govCode}
	 ,type=#{entity.type}
	 ,zwuid=#{entity.zwuid}
	 ,dduid=#{entity.dduid}
	 ,ico=#{entity.ico}
	,remark=#{entity.remark},email=#{entity.email},sort=#{entity.sort},updateTime=now()
where 
	id=#{entity.id} and uuid=#{entity.uuid}
