mapper.select:
select
from um_app_menu u where u.status <> 9 and u.id in
#{id}

SELECT
	
FROM
	um_app_menu u
WHERE
	u.status <> 9 AND u.appId = #{query.cnds.appId} 
     AND u.name like #{query.cnds.name}
	 AND u.status = #{query.cnds.status}
	 AND u.govCode = #{query.cnds.govCode}

	
		order by  u.${ord} 
	
	
		order by u.parentId,u.sort

mapper.update:
update 
	um_app_menu 
set 
	name=#{entity.name},sort=#{entity.sort},url=#{entity.url},type=#{entity.type},ico=#{entity.ico},remark=#{entity.remark}
	 ,govCode=#{entity.govCode}
	 ,parentId=#{entity.parentId},parentClass=#{entity.parentClass}
	 ,status=#{entity.status}
	,updateTime=now()
where 
	id=#{entity.id} and uuid=#{entity.uuid}
