mapper.select:
select
from um_yw_user u left join um_yw_department d on u.parentId=d.id AND u.parentClass='YwDepartment' left join um_yw_company c on u.companyId=c.id where u.id in
#{id}

SELECT
	
FROM um_yw_user u 
	left join um_yw_department d on u.parentId=d.id AND u.parentClass='YwDepartment'
	left join um_yw_company c on u.companyId=c.id 
WHERE
	u.status <> 9 AND u.account <> 'superadmin' 
	 
		AND ( c.areaId < 0 
			
		         OR c.area1Id = #{page.cnds.AND_0.level_1_eq} 
		         OR c.area1Id in #{areaId} 
		    
		    
		         OR c.area2Id = #{page.cnds.AND_0.level_2_eq} 
		         OR c.area2Id in #{areaId} 
		    
		    
		         OR c.area3Id = #{page.cnds.AND_0.level_3_eq} 
		         OR c.area3Id in #{areaId} 
		    
		    
		         OR c.area4Id = #{page.cnds.AND_0.level_4_eq} 
		         OR c.area4Id in #{areaId} 
		    
		    
		         OR c.area5Id = #{page.cnds.AND_0.level_5_eq} 
		         OR c.area5Id in #{areaId} 
		    
		    
		         OR c.area6Id = #{page.cnds.AND_0.level_6_eq} 
		         OR c.area6Id in #{areaId} 
		    
		    
		         OR c.area7Id = #{page.cnds.AND_0.level_7_eq} 
		         OR c.area7Id in #{areaId} 
		    
		    
		         OR c.area8Id = #{page.cnds.AND_0.level_8_eq} 
		         OR c.area8Id in #{areaId} 
		    
		)
	
	 AND u.companyId = #{page.cnds.companyId}
	 AND u.parentId = #{page.cnds.parentId} AND u.parentClass='YwDepartment'
	 AND u.parentClass = #{page.cnds.parentClass}
	 AND u.name like #{page.cnds.name}
	 AND u.idCard = #{page.cnds.idCard}
	 AND u.sex = #{page.cnds.sex}
	 AND u.govCode = #{page.cnds.govCode}
	 AND u.account = #{page.cnds.account}
	 AND u.mobile like #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}
	 AND u.ddStatus = #{page.cnds.ddStatus}
	 AND u.dyType = #{page.cnds.dyType}

	
		order by  u.${ord} 
	
	
		order by u.parentId

SELECT
	
FROM
	um_yw_user u 
	left join um_yw_department d on u.parentId=d.id AND u.parentClass='YwDepartment'
	left join um_yw_company c on u.companyId=c.id 
WHERE
	u.status <> 9 AND u.account <> 'superadmin' 
	 AND u.companyId = #{page.cnds.companyId}
	 AND u.parentId = #{page.cnds.parentId} AND u.parentClass='YwDepartment' 
	 AND u.name like #{page.cnds.name}
	 AND u.sex = #{page.cnds.sex}
	 AND u.account = #{page.cnds.account}
	 AND u.mobile like #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}
	 AND u.status <> #{page.cnds.statusNe}
	 AND u.idCard = #{page.cnds.idCard}
	 AND u.govCode = #{page.cnds.govCode}
	 AND u.dyType = #{page.cnds.dyType}

	
		order by  u.${ord} 
	
	
		order by u.parentId

SELECT
		u.*
FROM (
				SELECT
					yw.id,yw.uuid,yw.status,yw.createTime,yw.updateTime,yw.companyId,
					case when yw.parentClass='YwDepartment' then yw.parentId
					else yw.parentId*-1 
					end parentId,
					yw.parentClass,yw.account,yw.name,yw.nickName,yw.mobile,yw.idCard,yw.sex,yw.birthday,yw.remark,yw.lastLoginTime,
					yw.email,yw.sort,yw.type,yw.ico,yw.code,yw.govCode,yw.jobPosition,yw.job,yw.sms,yw.zwuid,yw.dduid,yw.ddStatus,yw.dyType,yw.rdDate,yw.dorg,
					d.name as orgName,d.fullName as orgFullName,c.name as companyName
				FROM
					um_yw_user yw 
					LEFT JOIN um_yw_department d on yw.parentId=d.id AND yw.parentClass='YwDepartment' 
					LEFT JOIN um_yw_company c on yw.companyId=c.id 
				WHERE
					yw.status <> 9 AND yw.status <> 8 AND yw.account <> 'superadmin' 
					AND yw.companyId = #{page.cnds.companyId}
		) u
		JOIN um_yw_perm_org p ON u.parentId=p.orgId
		JOIN um_yw_rel_role_user rel ON p.roleId=rel.roleId
WHERE	
		p.appId=#{page.cnds.appId} AND rel.userId=#{page.cnds.userId} AND rel.companyId=#{page.cnds.companyId} 
		 AND u.name like #{page.cnds.name}
		 AND u.sex = #{page.cnds.sex}
		 AND u.account = #{page.cnds.account}
		 AND u.mobile like #{page.cnds.mobile}
		 AND u.status = #{page.cnds.status}
		 AND u.idCard = #{page.cnds.idCard}
		 AND u.govCode = #{page.cnds.govCode}
		 AND u.dyType = #{page.cnds.dyType}

	
		order by  u.${ord} 
	
	
		order by u.parentId

