SELECT
		u.*
FROM (
				SELECT
					yw.id,yw.uuid,yw.status,yw.createTime,yw.updateTime,yw.companyId,
					case when yw.parentClass='YwDepartment' then yw.parentId
					else yw.parentId*-1 
					end parentId,
					yw.parentClass,yw.account,yw.name,yw.nickName,yw.mobile,yw.idCard,yw.sex,yw.birthday,yw.remark,yw.lastLoginTime,
					yw.email,yw.sort,yw.type,yw.ico,yw.code,yw.govCode,yw.jobPosition,yw.job,yw.sms,yw.zwuid,yw.dduid,yw.ddStatus,yw.dyType,yw.rdDate,yw.dorg,
					d.name as orgName,c.name as companyName
				FROM
					um_yw_user yw 
						LEFT JOIN um_yw_department d on yw.parentId=d.id
						LEFT JOIN um_yw_company c on yw.companyId=c.id
				WHERE
					yw.status <> 9 AND yw.account <> 'superadmin' 
					AND yw.companyId = #{page.cnds.companyId}
		) u
		JOIN um_yw_perm_org p ON u.parentId=p.orgId
		JOIN um_yw_rel_role_user rel ON p.roleId=rel.roleId
WHERE	
		p.appId=#{page.cnds.appId} AND rel.userId=#{page.cnds.userId} AND rel.companyId=#{page.cnds.companyId} 
	
		 AND u.id not in ( SELECT rel2.userId FROM um_yw_rel_role_user rel2 WHERE rel2.roleId = #{page.cnds.roleId} AND rel2.companyId=#{page.cnds.companyId} ) 
	
	 
		AND u.companyId = #{page.cnds.companyId}
	
	 
		AND u.companyId in (
			SELECT com.id FROM um_yw_company com WHERE com.parentId=#{page.cnds.companyId} OR com.id=#{page.cnds.companyId}
		) 
	
	 AND u.parentId = #{page.cnds.parentId}
	 AND u.name like #{page.cnds.name}
	 AND u.sex = #{page.cnds.sex}
	 AND u.account = #{page.cnds.account}
	 AND u.mobile = #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}
	 AND u.idCard = #{page.cnds.idCard}
	 AND u.govCode = #{page.cnds.govCode}
	 AND u.dyType = #{page.cnds.dyType}

	
		order by  u.${ord} 
	
	
		order by u.parentId

SELECT
	u.id,u.uuid,u.status,u.createTime,u.updateTime,
	u.companyId,u.parentId,u.parentClass,u.account,u.password,u.salt,u.name,u.nickName,u.mobile,u.idCard,u.sex,u.birthday,u.remark,u.lastLoginTime,
	u.email,u.sort,u.type,u.ico,u.code,u.govCode,u.jobPosition,u.job,u.sms,u.zwuid,u.dduid,u.ddStatus,u.dyType,u.rdDate,u.dorg,
	d.name as orgName,d.fullName as orgFullName,c.name as companyName,rel.companyId as jzCompanyId,pc.name as jzCompanyName
FROM
	um_yw_user u 
		left join um_yw_department d on u.parentId=d.id AND u.parentClass='YwDepartment' 
		left join um_yw_company c on u.companyId=c.id 
		join um_yw_rel_role_user rel on u.id=rel.userId
		join um_yw_company pc on rel.companyId=pc.id
WHERE
	u.status <> 9 AND rel.roleId = #{page.cnds.roleId} 
	 AND u.parentId = #{page.cnds.parentId}
	 AND u.name like #{page.cnds.name}
	 AND u.account = #{page.cnds.account}
	 AND u.mobile = #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}
	 AND u.idCard = #{page.cnds.idCard}
	 AND u.govCode = #{page.cnds.govCode}
	 AND u.dyType = #{page.cnds.dyType}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	u.id,u.uuid,u.status,u.createTime,u.updateTime,
	u.companyId,u.parentId,u.parentClass,u.account,u.password,u.salt,u.name,u.nickName,u.mobile,u.idCard,u.sex,u.birthday,u.remark,u.lastLoginTime,
	u.email,u.sort,u.type,u.ico,u.code,u.govCode,u.jobPosition,u.job,u.sms,u.zwuid,u.dduid,u.ddStatus,u.dyType,u.rdDate,u.dorg,d.name as orgName,d.fullName as orgFullName,c.name as companyName
FROM um_yw_user u 
	left join um_yw_department d on u.parentId=d.id AND u.parentClass='YwDepartment' 
	left join um_yw_company c on u.companyId=c.id 
WHERE
	u.status <> 9 AND u.status <> 8 AND u.account <> 'superadmin' 
	AND u.id not in ( SELECT rel.userId FROM um_yw_rel_role_user rel WHERE rel.roleId = #{page.cnds.roleId} AND rel.companyId = #{page.cnds.companyId} ) 
	AND u.companyId = #{page.cnds.companyId}
	 
		AND ( u.parentClass = 'YwCompany' OR d.type = 1 )
	
	 AND u.parentId = #{page.cnds.parentId}
	 AND u.name like #{page.cnds.name}
	 AND u.sex = #{page.cnds.sex}
	 AND u.account = #{page.cnds.account}
	 AND u.mobile = #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}
	 AND u.idCard = #{page.cnds.idCard}
	 AND u.govCode = #{page.cnds.govCode}
	 AND u.dyType = #{page.cnds.dyType}

	
		order by  u.${ord} 
	
	
		order by u.parentId

SELECT
	u.id,u.uuid,u.status,u.createTime,u.updateTime,u.companyId,u.parentId,u.parentClass,u.account,u.salt,
	u.name,u.nickName,u.mobile,u.idCard,u.sex,u.birthday,u.remark,u.lastLoginTime,
	u.email,u.sort,u.type,u.ico,u.code,u.govCode,u.jobPosition,u.job,u.sms,u.zwuid,u.dduid,u.ddStatus,u.dyType,u.rdDate,u.dorg
FROM
	um_yw_user u 
WHERE
	u.status <> 9
	 AND u.companyId = #{query.cnds.companyId}
	 AND u.parentId = #{query.cnds.parentId}
	 AND u.parentClass = #{query.cnds.parentClass}
	 AND u.name like #{query.cnds.name}
	 AND u.sex = #{query.cnds.sex}
	 AND u.account = #{query.cnds.account}
	 AND u.mobile like #{query.cnds.mobile}
	 AND u.mobile = #{query.cnds.mobileEq}
	 AND u.status = #{query.cnds.status}
	 AND u.ddStatus = #{query.cnds.ddStatus}
	 AND u.idCard = #{page.cnds.idCard}
	 AND u.govCode = #{page.cnds.govCode}
	 AND u.dyType = #{page.cnds.dyType}

	
		order by  u.${ord} 
	
	
		order by u.id

SELECT
	u.id,u.uuid,u.status,u.createTime,u.updateTime,u.parentId,u.parentClass,u.account,u.salt,
	u.name,u.nickName,u.mobile,u.idCard,u.sex,u.birthday,u.remark,u.lastLoginTime,
	u.email,u.type,u.ico,u.code,u.govCode,rel.jobPosition,rel.sort
FROM
	um_yw_user u join um_yw_rel_org_leader rel on u.id=rel.userId 
WHERE
	u.status <> 9 AND u.status <> 8 AND rel.orgId=#{page.cnds.orgId} 
	 AND u.name like #{page.cnds.name}
	 AND u.sex = #{page.cnds.sex}
	 AND u.mobile like #{page.cnds.mobile}
	 AND u.status = #{page.cnds.status}
order by rel.sort

mapper.update:
update 
	um_yw_user 
set 
	name=#{entity.name},nickName=#{entity.nickName},mobile=#{entity.mobile},sex=#{entity.sex},idCard=#{entity.idCard},birthday=#{entity.birthday}
	,jobPosition=#{entity.jobPosition},job=#{entity.job},sms=#{entity.sms}
	 ,companyId=#{entity.companyId}
	 ,parentId=#{entity.parentId},parentClass=#{entity.parentClass} 
	 ,password=#{entity.password},salt=#{entity.salt}
	 ,status=#{entity.status}
	 ,code=#{entity.code}
	 ,govCode=#{entity.govCode}
	 ,type=#{entity.type}
	 ,zwuid=#{entity.zwuid}
	 ,dduid=#{entity.dduid}
	 ,ico=#{entity.ico}
	 ,dyType=#{entity.dyType}
	,rdDate=#{entity.rdDate},dorg=#{entity.dorg},remark=#{entity.remark},email=#{entity.email},sort=#{entity.sort},updateTime=now()
where 
	id=#{entity.id} and uuid=#{entity.uuid}
